mapper.select:
select dcs.`id`, dcs.`doc_project_id`, dcs.`doc_category_id`, dcs.`uuid`, dcs.`share_type`,
dcs.`effective_type`, dcs.`deadline`, dcs.`created_at`, dcs.`creator_user_id`, dcs.`creator_nickname`, dcs.`updated_at`, dcs.`updator_user_id`, dcs.`updator_nickname`, dcs.`disabled`, dcs.`deleted`, dcs.`remark`, dp.id as `docProjectId`, dp.uuid as `docProjectUuid`, dp.project_name as `docProjectName`, dc.uuid as `docCategoryUuid`, dc.`text` as `docCategoryName`, case when dcs.`disabled` = 1 then 2 when dcs.`effective_type` <> -1 and date_format(now(), '%Y-%m-%d') > date_format(dcs.`deadline`, '%Y-%m-%d') then 3 else 1 end as `status` from doc_project dp, doc_category dc, doc_category_share dcs where dp.id = dc.doc_project_id and dc.id = dcs.doc_category_id and dp.deleted = 0 and dc.deleted = 0 and dcs.deleted = 0 and dcs.creator_user_id = #{user.userId,jdbcType=VARCHAR} and dp.id in
#{projectId,jdbcType=BIGINT}
and dp.uuid = #{form.docProjectUuid,jdbcType=VARCHAR}
and `status` = #{form.status,jdbcType=INTEGER}
and ( dp.project_name like concat('%', #{form.keywords,jdbcType=VARCHAR}, '%') or dc.`text` like concat('%', #{form.keywords,jdbcType=VARCHAR}, '%') or dc.`fullname` like concat('%', #{form.keywords,jdbcType=VARCHAR}, '%') )
order by dcs.id desc
